import { NavLink } from "react-router-dom";
import "./Home.css";
import { useEffect, useState } from "react";

export function Home(): JSX.Element {
    const [displayedText, setDisplayedText] = useState<string>("See");
    const texts = ["See", "The", "World"];
    let index = 0;

    useEffect(() => {
        const intervalId = setInterval(() => {
            index = (index + 1) % texts.length;
            setDisplayedText(texts[index]);
        }, 3000); // משנה את הטקסט כל 3 שניות

        return () => clearInterval(intervalId); // מנקה את ה-interval כאשר הקומפוננטה יוצאת משימוש
    }, []);

    return (
        <div className="Home">
            <span id="text" className="text">
                <p >{displayedText}</p>
            </span>
            <button className="navLinkBtn" id="allButton">
                <NavLink to="/countries">Click To See The World</NavLink>
            </button>
            <div id="countryCards"></div>
        </div>
    );
}
