import { useEffect, useState } from "react";
import { CountryModel } from "../../../Models/CountryModel";
import { countryService } from "../../../Services/CountryService";
import { CountryCard } from "../CountryCard/CountryCard";
import "./Countries.css";

interface CountriesProps {
    setSelectedCountries: React.Dispatch<React.SetStateAction<CountryModel[]>>;
}


export function Countries(): JSX.Element {

    const [countries, setCountries] = useState<CountryModel[]>([]);
    const [selectedCountries, setSelectedCountries] = useState<CountryModel[]>([]);
    const [filteredCountries, setFilteredCountries] = useState<CountryModel[]>([]);

    useEffect(() => {
        countryService.getAllCountries()
            .then(countries => {
                setCountries(countries);
                setFilteredCountries(countries)
            })
            .catch(err => console.error(err.message));
    }, []);

    useEffect(() => {
        const query = searchQuery.toLowerCase();
        const filtered = countries.filter(c =>
            c.name.common.toLocaleLowerCase().includes(query)
        );
        setFilteredCountries(filtered)
    })

    const handleCountryClick = (country: CountryModel) => {
        if (selectedCountries.includes(country)) {
            setSelectedCountries([...selectedCountries, country]);
        }
    }

    return (
        <div className="Countries">
            <form id="countryForm">
                <input type="text" id="countryInput" placeholder="Enter country name"></input>
                <button type="button" id="searchButton">Search Countries</button>
            </form>
            <div className="countryGrid">
                {countries.map(c => (
                    <CountryCard
                        key={c.name.common}
                        country={c} onClick={function (country: CountryModel): void {
                            throw new Error("Function not implemented.");
                        }} />
                ))}
            </div>
        </div>
    );
}
