import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { CountryModel } from "../../../Models/CountryModel";
import { countryService } from "../../../Services/CountryService";
import "./CountryDetails.css";

export function CountryDetails(): JSX.Element {
    const { name } = useParams<{ name: string }>(); 
    const [country, setCountry] = useState<CountryModel | null>(null);
    const [loading, setLoading] = useState<boolean>(true); // Loading state

    useEffect(() => {
        if (name) {
            countryService.getOneCountry(name)
                .then(fetchedCountry => {
                    setCountry(fetchedCountry);
                    setLoading(false); // Stop loading once data is fetched
                })
                .catch(err => {
                    console.error("Error fetching country:", err.message);
                    setLoading(false); // Stop loading even if there's an error
                });
        }
    }, [name]);

    // Show loading spinner with the flag if loading is true
    if (loading) {
        return (
            <div className="spinner">
                {country?.flags?.png && (
                    <img src={country.flags.png} alt="Loading flag..." />
                )}
            </div>
        );
    }

    // Show nothing if country data is null
    if (!country) return null;

    // Render the country details once data is loaded
    return (
        <div className="CountryDetails">
            <h1>{country.name.common}</h1> 
            <h2>Capital: {country.capital?.[0] || "N/A"}</h2>
            <h2>Population: {country.population.toLocaleString()}</h2>
            <h2>Continent: {country.continents?.join(", ") || "N/A"}</h2>
            <h2>UN Member: {country.unMember ? "Yes" : "No"}</h2>
            <img src={country.flags.png} alt={`${country.name.common} flag`} />
        </div>
    );
}
