import { useNavigate } from "react-router-dom";
import { CountryModel } from "../../../Models/CountryModel";
import "./CountryCard.css";

type CountryCardProps = {
    country: CountryModel;
    onClick: (country: CountryModel) => void;
};

export function CountryCard(props: CountryCardProps): JSX.Element {

    const navigate = useNavigate();

    function showDetails(): void {
        navigate("/country-details/" + props.country.name.co); // Access the `common` property correctly
    }

    // Extract languages as a string
    const languages = props.country.languages
        ? Object.values(props.country.languages).join(", ")
        : "No languages available";

    return (
        <div
            className="CountryCard"
            onClick={showDetails}
            role="button"
            aria-label={`View details of ${props.country.name.common}`}
        >
            <div className="CountryDetails">
                <img src={props.country.flags.png} alt={`${props.country.name.common} flag`} />
                <span>Country: {props.country.name.common}</span>
                <span>Capital City: {props.country.capital?.[0] || "N/A"}</span>
                <span>Population: {props.country.population.toLocaleString()}</span>
                <span>Spoken Languages: {languages}</span>
                <span>Is UN Member: {props.country.unMember ? "Yes" : "No"}</span>
            </div>
        </div>
    );
}
